CREATE TABLE items (
    item_id UUID PRIMARY KEY DEFAULT uuid_generate_v4(),
    name TEXT NOT NULL,
    category TEXT NOT NULL,
    liked BOOLEAN DEFAULT FALSE
);

CREATE TABLE thumbnails (
    image_id UUID PRIMARY KEY DEFAULT uuid_generate_v4(),
    image TEXT NOT NULL,           
    item_id UUID NOT NULL,
    CONSTRAINT fk_thumbnails_item
        FOREIGN KEY (item_id)
        REFERENCES items(item_id)
        ON DELETE CASCADE
);

CREATE TABLE infos (
    image_id UUID  PRIMARY KEY
    qty INTEGER NOT NULL,
    cost NUMERIC(10, 2) NOT NULL,
    details TEXT(500) NOT NULL,
    item_id UUID NOT NULL,
    CONSTRAINT fk_infos_item
        FOREIGN KEY (item_id)
        REFERENCES items(item_id)
        ON DELETE CASCADE
);

CREATE TABLE orders (
    item_id UUID NOT NULL,
    info_id UUID NOT NULL,
    order_qty INTEGER NOT NULL,
    created_at DATE DEFAULT CURRENT_DATE,
    CONSTRAINT fk_orders_item
        FOREIGN KEY (item_id)
        REFERENCES items(item_id)
        ON DELETE CASCADE,
    CONSTRAINT fk_orders_info
        FOREIGN KEY (info_id)
        REFERENCES infos(info_id)
        ON DELETE CASCADE
);

